(5, 'Ajax Live Options', 'ajax-live-options', 'Denise (rei7092@gmail.com)', '2.1.1.0', 'http://www.jindolure.com/', '<?xml version="1.0" encoding="utf-8"?>\n<modification>\n    <name>Ajax Live Options</name>\n    <code>ajax-live-options</code>\n    <version>2.1.1.0</version>\n    <author>Denise (rei7092@gmail.com)</author>\n    <link>http://www.jindolure.com/</link>\n    <file path="catalog/model/catalog/product.php">\n        <operation>\n            <search>\n               <![CDATA[''weight''                  => $product_option_value[''weight''],]]>\n            </search>\n            <add position="before">\n            <![CDATA[\n            // Ajax Live Options\n            ''points''        => $product_option_value[''points''],\n            ''points_prefix'' => $product_option_value[''points_prefix''],\n            ]]>\n            </add>\n        </operation>\n    </file>\n    <file path="catalog/view/theme/*/template/product/product.tpl">\n        <operation>\n            <search>\n               <![CDATA[<?php echo $footer; ?>]]>\n            </search>\n            <add position="before">\n            <![CDATA[\n            <script type="text/javascript" src="index.php?route=product/live_options/js&product_id=<?php echo $product_id; ?>"></script>\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n               <![CDATA[<?php echo $price; ?>]]>\n            </search>\n            <add position="replace">\n            <![CDATA[\n            <span class="<?php echo $live_options[''live_options_price_container'']; ?>"><?php echo $price; ?></span>\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n               <![CDATA[<li><?php echo $text_points; ?> <?php echo $points; ?></li>]]>\n            </search>\n            <add position="replace">\n            <![CDATA[\n            <li class="<?php echo $live_options[''live_options_points_container'']; ?>"><?php echo $text_points; ?> <?php echo $points; ?></li>\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n               <![CDATA[<li><?php echo $text_reward; ?> <?php echo $reward; ?></li>]]>\n            </search>\n            <add position="replace">\n            <![CDATA[\n            <li class="<?php echo $live_options[''live_options_reward_container'']; ?>"><?php echo $text_reward; ?> <?php echo $reward; ?></li>\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n               <![CDATA[<li><?php echo $text_tax; ?> <?php echo $tax; ?></li>]]>\n            </search>\n            <add position="replace">\n            <![CDATA[\n            <li class="<?php echo $live_options[''live_options_tax_container'']; ?>"><?php echo $text_tax; ?> <?php echo $tax; ?></li>\n            ]]>\n            </add>\n        </operation>\n        <operation>\n            <search>\n               <![CDATA[<h2><?php echo $special; ?></h2>]]>\n            </search>\n            <add position="replace">\n            <![CDATA[\n            <h2 class="<?php echo $live_options[''live_options_special_container'']; ?>"><?php echo $special; ?></h2>\n            ]]>\n            </add>\n        </operation>\n    </file>\n    <file path="catalog/controller/product/product.php">\n        <operation info="Define CSS calss name">\n            <search><![CDATA[$data[''products''] = array();]]></search>\n            <add position="before"><![CDATA[\n            // Ajax Live Options\n            if($this->config->get(''live_options_ajax_status'')){\n                $data[''live_options''] = array(\n                    ''live_options_container'' => substr($this->config->get(''live_options_container''), 1),\n                    ''live_options_special_container'' => substr($this->config->get(''live_options_special_container''), 1),\n                    ''live_options_price_container'' => substr($this->config->get(''live_options_price_container''), 1),\n                    ''live_options_tax_container'' => substr($this->config->get(''live_options_tax_container''), 1),\n                    ''live_options_points_container'' => substr($this->config->get(''live_options_points_container''), 1),\n                    ''live_options_reward_container'' => substr($this->config->get(''live_options_reward_container''), 1),\n                );\n            }\n            ]]></add>\n        </operation>\n        <operation info="Show Full Option Price">\n            <search><![CDATA[foreach ($option[''product_option_value''] as $option_value) {]]></search>\n            <add position="after"><![CDATA[\n            // show total amount\n            if($this->config->get(''live_options_ajax_status'') && !$this->config->get(''live_options_show_options_type'') && $option_value[''price''] > 0){\n                if ($option_value[''price_prefix''] == ''-'' && $option_value[''price'']) {\n                    $option_value[''price''] = (($product_info[''special''] ? ($product_info[''special''] - $option_value[''price'']) : ($product_info[''price'']) - $option_value[''price'']));\n                } elseif ($option_value[''price_prefix''] == ''+'' && $option_value[''price'']) {\n                    $option_value[''price''] = (($product_info[''special''] ? ($product_info[''special''] + $option_value[''price'']) : ($product_info[''price'']) + $option_value[''price'']));\n                }\n                $option_value[''price_prefix''] = '''';\n            }\n            ]]></add>\n        </operation>\n        <operation info="Show Full Option Price">\n            <search><![CDATA[\n                $price = $this->currency->format($this->tax->calculate($option_value[''price''], $product_info[''tax_class_id''], $this->config->get(''config_tax'') ? ''P'' : false));\n                ]]></search>\n            <add position="replace"><![CDATA[\n            // show total amount\n            if($this->config->get(''live_options_ajax_status'') && !$this->config->get(''live_options_show_options_type'')){\n                $price = $this->currency->format($this->tax->calculate($option_value[''price''], $product_info[''tax_class_id''], $this->config->get(''config_tax'')));\n            }\n            else{\n                $price = $this->currency->format($this->tax->calculate($option_value[''price''], $product_info[''tax_class_id''], $this->config->get(''config_tax'') ? ''P'' : false));\n            }\n\n\n            ]]></add>\n        </operation>\n    </file>\n</modification>\n', 0, '2016-05-22 20:54:18'),
(11, 'Product picture on e-mail', 'product_picture_mail', 'Luis Fernando Sacchi Ribeiro', '1.0', '', '<?xml version="1.0" encoding="utf-8"?>\n<modification>\n    <code>product_picture_mail</code>\n    <name>Product picture on e-mail</name>\n    <version>1.0</version>\n    <author>Luis Fernando Sacchi Ribeiro</author>\n\n    <file path="catalog/view/theme/*/template/mail/order.tpl">\n        <operation>\n            <search trim="true"><![CDATA[\n				<?php echo $product[''name'']; ?>\n            ]]></search>\n            <add position="replace" trim="false" offset="0"><![CDATA[\n                <img src="<?php echo $product[''thumb'']; ?>" title="<?php echo $product[''name'']; ?>" style="vertical-align:middle;" /> <?php echo $product[''name'']; ?>\n            ]]></add>\n        </operation>\n    </file>\n    <file path="catalog/model/checkout/order.php">\n        <operation>\n            <search trim="true"><![CDATA[\n            $this->load->model(''tool/upload'');\n            ]]></search>\n            <add position="before" trim="false" offset="0"><![CDATA[\n            $this->load->model(''tool/image'');\n            ]]></add>\n        </operation>\n        <operation>\n            <search trim="true"><![CDATA[\n            $option_data = array();\n            ]]></search>\n            <add position="after" trim="false" offset="0"><![CDATA[\n            if ($product[''image'']) {\n                $image = $this->model_tool_image->resize($product[''image''], $this->config->get(''config_image_cart_width''), $this->config->get(''config_image_cart_height''));\n            } else {\n                $image = '''';\n            }\n            ]]></add>\n        </operation>\n        <operation>\n            <search trim="true"><![CDATA[\n            $data[''products''][] = array(\n            ]]></search>\n            <add position="after" trim="false" offset="0"><![CDATA[\n            ''thumb''    => $image,\n            ]]></add>\n        </operation>\n        <operation>\n            <search trim="true"><![CDATA[\n            $order_product_query = $this->db->query("SELECT * FROM " . DB_PREFIX . "order_product WHERE order_id = ''" . (int)$order_id . "''");\n            ]]></search>\n            <add position="replace" trim="false" offset="0"><![CDATA[\n            $order_product_query = $this->db->query("SELECT " . DB_PREFIX . "order_product.*," . DB_PREFIX . "product.image FROM " . DB_PREFIX . "order_product LEFT JOIN " . DB_PREFIX . "product ON " . DB_PREFIX . "order_product.product_id = " . DB_PREFIX . "product.product_id WHERE order_id = ''" . (int)$order_id . "''");\n            ]]></add>\n        </operation>\n    </file>\n</modification>\n', 1, '2016-06-22 06:56:53'),
(7, 'EmailVerification', 'isenselabs_emailverification', 'iSenseLabs', '1.2.2', 'http://isenselabs.com', '<modification>\n	<name>EmailVerification</name>\n	<version>1.2.2</version>\n	<link>http://isenselabs.com</link>\n	<author>iSenseLabs</author>\n	<code>isenselabs_emailverification</code>\n  \n  	<file path="catalog/model/account/customer.php">\n		<operation>\n			<search><![CDATA[if ($this->config->get(''config_account_mail'')) {]]></search>\n			<add position="before" offset="1"><![CDATA[\n				\n				$this->load->model(''setting/setting'');			\n				$emailverification = $this->model_setting_setting->getSetting(''emailverification'', $this->config->get(''config_store_id''));\n				$skip_verification = false;	\n				if(isset($this->session->data[''skipverification'']) && $this->session->data[''skipverification''] == true)\n				{\n					$skip_verification = true;\n					unset($this->session->data[''skipverification'']);\n				}\n				if(!$skip_verification && $emailverification[''emailverification''][''enabled''] == 1){\n					$this->load->model(''module/emailverification'');\n					if($this->model_module_emailverification->validateCustomerGroup($customer_group_id)){\n						$this->model_module_emailverification->sendVerificationEmail($customer_id);\n					}\n				}\n			]]></add>\n		</operation>\n	</file>	\n\n	<file path="catalog/view/theme/*/template/account/login.tpl">\n		<operation>\n			<search><![CDATA[ <div class="row"><?php echo $column_left; ?>]]></search>\n			<add position="before"><![CDATA[\n				<?php if ($error_warning_verified) { ?>\n  				<div class="alert alert-danger"><i class="fa fa-exclamation-circle"></i> <?php echo $error_warning_verified; ?></div>\n  				<?php } ?>\n			]]></add>\n		</operation>\n	</file>\n\n	<file path="catalog/controller/account/success.php">\n		<operation>\n			<search><![CDATA[$data[''column_left''] = $this->load->controller(''common/column_left'');]]></search>\n			<add position="before"><![CDATA[\n				$this->load->model(''setting/setting'');